mapper.select:
select t.* from( select g.*,d.fileName,ROW_NUMBER() OVER (ORDER BY g.goods_creDate DESC) as recNum from t_shopping_goods g, t_goods_detail_image d where g.goods_id=d.goods_id and d.filetype='main_image' and goods_sort=#{sort}
and goods_status like CONCAT('%', #{goods_status}, '%')
order by g.goods_creDate desc) t where recNum between (#{section}-1)*100+ (#{pageNum}-1)*10+1 and (#{section}-1)*100+(#{pageNum})*10


    	select * from  t_goods_detail_image 
    	where  fileType!='main_image'
		and goods_id=#{goods_id}
