mapper.select:
     
      select * from member 
	   where 
	   id=#{id} and pwd=#{pwd}


  select id from member
  where 
  name=#{name} and phone=#{phone}


  select * from member
  where 
  name=#{name} and phone=#{phone} and id=#{id}

mapper.update:
    
      update member 
      set pwd=#{pwd}
      where 
      id=#{id}
